mapper.select:
SELECT id, name, leader, address_id, point, monthly_fee, created_at, updated_at
FROM club
WHERE name = #{name}

mapper.update:
UPDATE club
SET name = #{name}, updated_at = now()
WHERE id = #{id}

UPDATE club
SET address_id = #{addressId}, updated_at = now()
WHERE id = #{id}

UPDATE club
SET monthly_fee = #{monthlyFee}, updated_at = now()
WHERE id = #{id}
