mapper.select:
SELECT EXISTS(
               SELECT *
               FROM club_member
               WHERE club_id = #{clubId}
                 AND member_id = #{memberId}
                 AND grade = 'LEADER'
           )

SELECT EXISTS(
               SELECT *
               FROM club_member
               WHERE club_id = #{clubId}
                 AND member_id = #{memberId}
                 AND (grade = 'LEADER' OR grade = 'STAFF')
           )

SELECT EXISTS(
               SELECT *
               FROM club_member
               WHERE club_id = #{clubId}
                 AND member_id = #{memberId}
                 AND approved = 1
           )

SELECT EXISTS(
               SELECT *
               FROM club_member
               WHERE club_id = #{clubId}
                 AND member_id = #{memberId}
           )

SELECT id, club_id, member_id, grade, approved, created_at, updated_at
FROM club_member
WHERE id = #{id}

mapper.delete:
DELETE
FROM club_member
WHERE club_id = #{clubId}
  AND member_id = #{memberId}
