mapper.update:
UPDATE club_soccer_match_record
VALUES win = win + 1
WHERE club_id = #{clubId}

UPDATE club_soccer_match_record
VALUES draw = draw + 1
WHERE club_id = #{clubId}

UPDATE club_soccer_match_record
VALUES lose = lose + 1
WHERE club_id = #{clubId}

mapper.select:
SELECT EXISTS(
               SELECT *
               FROM club_soccer_match_record
               WHERE club_id = #{clubId}
           )
