mapper.select:
SELECT id, club1_score, club2_score, soccer_match_recruitment_id, created_at, updated_at
FROM soccer_match
WHERE id = #{id}

SELECT id, club1_score, club2_score, soccer_match_recruitment_id, created_at, updated_at
FROM soccer_match
WHERE soccer_match_recruitment_id = #{soccerMatchRecruitmentId}

SELECT club1_score
FROM soccer_match
WHERE id = #{id}

SELECT club2_score
FROM soccer_match
WHERE id = #{id}

mapper.update:
UPDATE soccer_match
SET submitted = true
WHERE id = #{id}

mapper.select:
SELECT EXISTS(
               SELECT * FROM soccer_match WHERE id = #{id} AND submitted = true
           )
