mapper.select:
SELECT id, club1_score, club2_score, soccer_match_recruitment_id, created_at, updated_at
FROM soccer_match
WHERE id = #{id}

SELECT id, club1_score, club2_score, soccer_match_recruitment_id, created_at, updated_at
FROM soccer_match
WHERE soccer_match_recruitment_id = #{soccerMatchRecruitmentId}

mapper.update:
UPDATE soccer_match
SET club1_score = club1_score + 1
WHERE id = #{id}

UPDATE soccer_match
SET club2_score = club2_score + 1
WHERE id = #{id}
